def first_term():
    return 1 / 3

def common_ratio():
    return (1 / 6) / (1 / 3)

def sum_of_infinite_geometric_series(a, r):
    return a / (1 - r)

# Calculate
a = first_term()
r = common_ratio()
result = sum_of_infinite_geometric_series(a, r)

print(result)

def growth_first_year(x):
    return x

def growth_second_year(x):
    return growth_first_year(x) * 1.5

def growth_third_year(x):
    return growth_second_year(x) * 1.5

def growth_fourth_year(x):
    return growth_third_year(x) * 2

def growth_fifth_year(x):
    return growth_fourth_year(x) * 0.5

def total_growth(x):
    return (growth_first_year(x) +
            growth_second_year(x) +
            growth_third_year(x) +
            growth_fourth_year(x) +
            growth_fifth_year(x))

def find_x(total_height):
    for x in range(1, total_height + 1):
        if total_growth(x) == total_height:
            return x
    return None

total_height = 23
x_value = find_x(total_height)
print(x_value)

